mapper.select:
SELECT DISTINCT tsn.news_id, tsn.news_title, tsn.news_content, tsn.news_author, tsn.news_show, tsn.show_time, tsn.opt_user AS opt_user_id, tsuopt.user_name AS opt_user_name, tsn.opt_time FROM tbl_sys_news tsn LEFT JOIN tbl_sys_user tsuopt ON tsn.opt_user = tsuopt.user_id
tsn.news_id = #{newsPojo.newsId}
tsn.news_show = #{newsPojo.newsShow}
AND tsn.news_title LIKE CONCAT('%',CONCAT(#{newsPojo.newsTitle},'%'))
AND tsn.news_content LIKE CONCAT('%',CONCAT(#{newsPojo.newsContent},'%'))
ORDER BY tsn.show_time DESC
LIMIT #{newsPojo.rowIndex},#{newsPojo.rowCount}
